mapper.update:
UPDATE product
PROD_NAME=#{prodName}, PROD_DETAIL=#{prodDetail}, MANUFACTURE_DAY=#{manuDate}, PRICE=#{price}, IMAGE_FILE=#{fileName}
WHERE PROD_NO=#{prodNo}

mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT p.prod_no, p.prod_name, p.price, p.reg_date, (SELECT t.tran_no FROM transaction t WHERE t.prod_no=p.prod_no and t.tran_status_code !='999') as tran_no, (SELECT t.tran_status_code FROM transaction t WHERE t.prod_no=p.prod_no and t.tran_status_code !='999') as tran_status_code FROM product p
p.prod_no like '%'|| #{searchKeyword} || '%'
p.prod_name like '%'|| #{searchKeyword} || '%'
p.price = #{searchKeyword}
ORDER BY prod_no DESC) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

SELECT COUNT(*) FROM( SELECT prod_no , prod_name, price, reg_date FROM product
prod_no = '%'|| #{searchKeyword} || '%'
prod_name = '%'|| #{searchKeyword} || '%'
price = #{searchKeyword}
) countTable
